# Set the wallpaper
$imageUrl = "data:image/jpeg;base64,"
$outputPath = "$env:TEMP\wallpaper.jpg"
Invoke-WebRequest -Uri $imageUrl -OutFile $outputPath
Add-Type -TypeDefinition @"
using System;
using System.Runtime.InteropServices;
public class Wallpaper {
    [DllImport("user32.dll", CharSet = CharSet.Auto)]
    public static extern int SystemParametersInfo(int uAction, int uParam, string lpvParam, int fuWinIni);
    public const int SPI_SETDESKWALLPAPER = 20;
    public const int SPIF_UPDATEINIFILE = 0x01;
    public const int SPIF_SENDCHANGE = 0x02;
}
"@
[Wallpaper]::SystemParametersInfo([Wallpaper]::SPI_SETDESKWALLPAPER, 0, $outputPath, [Wallpaper]::SPIF_UPDATEINIFILE -bor [Wallpaper]::SPIF_SENDCHANGE)

# Set volume to max
Add-Type -TypeDefinition @"
using System;
using System.Runtime.InteropServices;
public class Volume {
    [DllImport("user32.dll")]
    public static extern int SendMessageW(IntPtr hWnd, int Msg, IntPtr wParam, IntPtr lParam);
    public const int WM_APPCOMMAND = 0x319;
    public const int APPCOMMAND_VOLUME_MAX = 0xA0000;
}
"@
$hwnd = Get-Process -Id (Get-Process -Name explorer).Id | Select-Object -ExpandProperty MainWindowHandle
[Volume]::SendMessageW($hwnd, [Volume]::WM_APPCOMMAND, $hwnd, [Volume]::APPCOMMAND_VOLUME_MAX)

# Download and play mp4 sound
$soundUrl = "https://github.com/y-uusuf/okok/raw/refs/heads/main/mixkit-dog-barkingtwice-1.mp4"
$soundPath = "$env:TEMP\sound.mp4"
Invoke-WebRequest -Uri $soundUrl -OutFile $soundPath
$runScript = {
    Add-Type -AssemblyName presentationCore
    $player = [System.Windows.Media.MediaPlayer]::new()
    $player.Open([Uri]::new('$soundPath'))
    
    # Loop the sound indefinitely
    while ($true) {
        $player.Play()
        Start-Sleep -Seconds $player.NaturalDuration.TimeSpan.TotalSeconds
    }
}

Start-Job -ScriptBlock $runScript
